#elif defined(ECS_VERBOSITY_1)
#define ecs_trace_1(...) ecs_trace(1, __VA_ARGS__);
#define ecs_trace_2(...)
#define ecs_trace_3(...)
#endif

#else

#define ecs_trace_1(...)
#define ecs_trace_2(...)
#define ecs_trace_3(...)

#endif

